def main(argv):
    inputfile = ''
    outputfile = ''
    try:
        opts, args = getopt.getopt(argv, "hi:o:", ["ifile=", "ofile="])
    except getopt.GetoptError:
        print('getInsertStatements.py -i <inputfile>.db3')
        sys.exit(2)
    for opt, arg in opts:
        if opt == '-h':
            print('getInsertStatements.py -i <inputfile>.db3')
            sys.exit()
        elif opt in ("-i", "--ifile"):
            inputfile = arg
            inputfileNameExt = inputfile.split('.')
            # print(inputfileNameExt[0] + " " + inputfileNameExt[1])
        elif opt in ("-o", "--ofile"):
            outputfile = arg
    try:
        convert_to_csv("", inputfileNameExt[0], inputfileNameExt[1])
        import_to_sql(inputfileNameExt[0])
        print("Success: " + inputfileNameExt[0])
    except Exception as e:
        print("Failed: " + inputfileNameExt[0])
        print(e)





def convert_to_csv(directory, db_name, file_ext):
    conn = sqlite3.connect((directory + '/' + db_name + '.' + file_ext))
    print((directory + '/' + db_name + '.' + file_ext))
    cursor = conn.cursor()
    tables = [
        "JOB",
        "JOBVAR",
        "PROJECT",
        "NODE",
        "SECTION",
        "NODINSP",
        "SECHIST",
        "SECINSP",
        "SECOBS",
        "SECOBSMM",
        "SECSTAT",
        "USRLIST",
        "USRVER",
    ]
    for table in tables:
        cursor.execute('SELECT * FROM ' + table)
        column_names = [column_name[0] for column_name in cursor.description]

        with open((directory + '/' + table + '.csv'), 'w', newline='') as csv_file:
            print(csv_file)
            csv_writer = csv.writer(csv_file)
            csv_writer.writerow(column_names)

            row = cursor.fetchone()
            while row is not None:
                # Check if the row is not empty (you can customize this check based on your data structure)
                if any(row):
                    csv_writer.writerow(row)

                row = cursor.fetchone()


def import_to_sql(databaseName, directory):
    print(databaseName)
    create_database(databaseName)
    create_tables(databaseName)
    connection = pymysql.connect(
        host=mysql_host,
        user=mysql_user,
        password=mysql_password,
        database=databaseName,
        cursorclass=pymysql.cursors.DictCursor
    )

    tables = [
        "JOB",
        "JOBVAR",
        "PROJECT",
        "NODE",
        "SECTION",
        "NODINSP",
        "SECHIST",
        "SECINSP",
        "SECOBS",
        "SECOBSMM",
        "SECSTAT",
        "USRLIST",
        "USRVER",
    ]

    for table in tables:
        count = 0
        file = table + ".csv"
        print(file)
        file = (directory + '/' + file)
        print(file)
        with open(file, 'r') as f:
            reader = csv.reader(f)
            columns = next(reader)
            cursor = connection.cursor()
            for data in reader:
                count += 1
                query = 'insert into ' + table + '({0}) values ({1})'
                query = query.format(','.join(columns), ','.join('?' * len(columns)))
                #print(query)
                for record in data:
                    # print(record)
                    if record.isnumeric():
                        query = query.replace("?", str(record), 1)
                    elif isinstance(record, float):
                        query = query.replace("?", str(record), 1)
                    elif record == "":
                        query = query.replace("?", "NULL", 1)
                    else:
                        record = record.replace("\"", "'")
                        query = query.replace("?", "\"" + record + "\"", 1)

                query = query.replace("?", "NULL")
                query = query.replace("\"\"", "\"")
                # print(query)
                cursor.execute(query)
                connection.commit()
            # print(str(count) + " rows inserted into table: " + table)


